var db = require("../models"); // Defining methods for the booksController


module.exports = {
  findAll: function findAll(req, res) {
    db.Book.find(req.query).sort({
      date: -1
    }).then(function (dbModel) {
      return res.json(dbModel);
    }).catch(function (err) {
      return res.status(422).json(err);
    });
  },
  findById: function findById(req, res) {
    db.Book.findById(req.params.id).then(function (dbModel) {
      return res.json(dbModel);
    }).catch(function (err) {
      return res.status(422).json(err);
    });
  },
  create: function create(req, res) {
    db.Book.create(req.body).then(function (dbModel) {
      return res.json(dbModel);
    }).catch(function (err) {
      return res.status(422).json(err);
    });
  },
  update: function update(req, res) {
    db.Book.findOneAndUpdate({
      _id: req.params.id
    }, req.body).then(function (dbModel) {
      return res.json(dbModel);
    }).catch(function (err) {
      return res.status(422).json(err);
    });
  },
  remove: function remove(req, res) {
    db.Book.findById({
      _id: req.params.id
    }).then(function (dbModel) {
      return dbModel.remove();
    }).then(function (dbModel) {
      return res.json(dbModel);
    }).catch(function (err) {
      return res.status(422).json(err);
    });
  }
};
